        public static string BuildMainCategory<T>(ProductType type, T item)
        {
            string mainCategory = string.Empty;
            var itemData = item as JohnHardyDataItem;

            if (itemData != null)
            {
                if (type == ProductType.Bracelet)
                {
                    mainCategory = "Bracelets & Bangles";
                }
                else if (type == ProductType.Earrings)
                {
                    mainCategory = "Earrings";
                }
                else if (type == ProductType.Necklace)
                {
                    mainCategory = "Necklaces & Pendants";
                }
                else if (type == ProductType.Cufflinks)
                {
                    mainCategory = "Cufflinks";
                }
                else if (type == ProductType.Ring)
                {
                    mainCategory = "Fashion Rings";
                }
            }

            return mainCategory;
        }

